import {useState} from "react";
import emailjs from "emailjs-com";
import {successToast, Toast} from "../util/toast";

export default function Contact(props) {

    const [data, setData] = useState({
        name: "",
        email: "",
        message: ""
    })

    function handleChange(event) {
        setData(prev => ({
            ...prev,
            [event.target.name]: event.target.value
        }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (!validateData()) return;
        props.setLoading(true);

        const templateParams = {
            from_name: data.name,
            from_email: data.email,
            message: data.message
        };

        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams, 'YOUR_USER_ID')
            .then(() => {
                props.setLoading(false);
                successToast("Message Sent");
                clearData();
            })
            .catch(err => {
                props.setLoading(false);
                Toast("Failed to send message");
            });
    }

    function clearData() {
        setData({
            name: "",
            email: "",
            message: ""
        });
    }

    function validateData() {
        if (data.name.length < 3) {
            Toast("Invalid Name");
            return false;
        }
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
            Toast("Invalid Email");
            return false;
        }
        if (data.message.length < 3) {
            Toast("Enter a valid message");
            return false;
        }
        return true;
    }

    return (
        <div className="h-full flex sm:flex-row flex-col justify-center font-['Work_Sans'] mt-12 bg-gradient-to-r from-purple-500 to-indigo-600 p-8 rounded-lg shadow-xl">

            <div className="sm:w-2/5">
                <p className="text-3xl sm:text-5xl text-white font-bold px-6">Connect With Us</p>
                <p className="text-lg sm:text-2xl text-gray-200 px-6 mt-2">We would love to respond to your queries. Feel free to get in touch with us.</p>
                <form onSubmit={handleSubmit} className="flex flex-col px-6 py-6 mt-6 bg-white rounded-2xl shadow-md p-8">
                    <div className="mb-4">
                        <label className="sm:text-xl text-gray-700 mb-1 font-semibold">Full Name</label>
                        <input onChange={handleChange} name="name" value={data.name} className="bg-gray-100 rounded-lg h-12 px-6 w-full border-2 border-gray-300 focus:border-indigo-500 focus:outline-none" type="text"/>
                    </div>
                    <div className="mb-4">
                        <label className="sm:text-xl text-gray-700 mb-1 font-semibold">Email</label>
                        <input onChange={handleChange} name="email" value={data.email} className="bg-gray-100 rounded-lg h-12 px-6 w-full border-2 border-gray-300 focus:border-indigo-500 focus:outline-none" type="email"/>
                    </div>
                    <div>
                        <label className="sm:text-xl text-gray-700 mb-1 font-semibold">Message</label>
                        <textarea onChange={handleChange} name="message" value={data.message} rows="4" className="bg-gray-100 rounded-lg px-6 py-4 w-full border-2 border-gray-300 focus:border-indigo-500 focus:outline-none" />
                    </div>
                    <button type="submit" className="w-full mt-4 transition duration-500 ease-in-out h-12 bg-indigo-500 hover:bg-indigo-600 rounded-lg text-white font-semibold">Submit</button>
                </form>
            </div>

            <div className="hidden sm:flex items-center w-2/5 ml-8">
                <img className="transition duration-500 ease-in-out hover:scale-95 rounded-2xl shadow-lg" alt="" src="https://i.ibb.co/P1zYDgh/image.jpg"/>
            </div>
        </div>
    );
}
