import {Page} from "../util/config";
import {useState} from "react";
import validator from "validator/es";
import {successToast, Toast} from "../util/toast";
import axios from "axios";
import {api} from "../static/config";

export default function Footer(props) {

    const[email, setEMail] = useState("")

    function handleChange(event) {
        setEMail(event.target.value)
    }

    function handleSubmit() {
        if (validator.isEmail(email)) {
            axios.post(`${api.url}/api/digest`, {email: email})
                .then(() => {
                    successToast("Thank You For Subscribing!")
                    clearData()
                })
                .catch(err => {
                    Toast(err.response.data.message)
                    clearData()
                })
        }
        else Toast("Invalid Email")
    }

    function clearData() {
        setEMail("")
    }

    return (
        <div className="bg-black text-cyan-400 font-mono">
        <div className="mt-24 flex justify-around flex-col sm:flex-row px-4 sm:px-0">
            <div className="mt-6">
                <div className="flex items-center">
                    <img className="w-6 h-6" src="https://img.icons8.com/material-rounded/48/22D1EE/cyber-security.png" alt="Cyber Security Icon" />
                    <p className="sm:text-lg ml-2 font-bold uppercase">GraphicGate</p>
                </div>
                <p className="text-gray-400 mt-2 sm:mt-4 text-sm">System Security Protocol</p>
            </div>

            <div className="mt-6">
                <p className="text-lg font-bold uppercase">Access Points</p>
                <p onClick={() => props.setPage(Page.ABOUT)} className="text-gray-400 mt-2 cursor-pointer hover:text-cyan-300">System Info</p>
                <p onClick={() => props.setPage(Page.CONTACT)} className="text-gray-400 cursor-pointer hover:text-cyan-300">Comms Relay</p>
            </div>

            <div className="hidden sm:block mt-6">
                <p className="text-lg font-bold uppercase">Data Stream</p>
                <p className="text-gray-400 mt-4 text-sm">Receive System Alerts & Updates.</p>
                <div className="flex mt-4">
                    <input
                        value={email}
                        onChange={handleChange}
                        className="bg-gray-800 text-cyan-400 rounded-md px-4 py-2 border border-cyan-600 focus:outline-none focus:ring-2 focus:ring-cyan-600"
                        placeholder="Target Node"
                    />
                    <button
                        onClick={handleSubmit}
                        className="transition duration-300 ease-in-out bg-cyan-600 text-black rounded-md px-4 py-2 ml-2 border border-cyan-600 hover:bg-transparent hover:text-cyan-600"
                    >
                        Connect
                    </button>
                </div>
            </div>
        </div>
        <hr className="border-t border-gray-700 my-8 mx-auto w-3/4" />
        <p className="text-gray-500 text-center pb-4 text-sm">github.com/prathamesh-a</p>
    </div>
    )
}
