import { useState } from "react";
import emailjs from "emailjs-com";
import { successToast, Toast } from "../util/toast";

export default function Contact(props) {

    const [data, setData] = useState({
        name: "",
        email: "",
        message: ""
    });

    function handleChange(event) {
        setData(prev => ({
            ...prev,
            [event.target.name]: event.target.value
        }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (!validateData()) return;
        props.setLoading(true);

        const templateParams = {
            from_name: data.name,
            from_email: data.email,
            message: data.message
        };

        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams, 'YOUR_USER_ID')
            .then(() => {
                props.setLoading(false);
                successToast("Message Sent");
                clearData();
            })
            .catch(err => {
                props.setLoading(false);
                Toast("Failed to send message");
            });
    }

    function clearData() {
        setData({
            name: "",
            email: "",
            message: ""
        });
    }

    function validateData() {
        if (data.name.length < 3) {
            Toast("Invalid Name");
            return false;
        }
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
            Toast("Invalid Email");
            return false;
        }
        if (data.message.length < 3) {
            Toast("Enter a valid message");
            return false;
        }
        return true;
    }

    return (
        <div className="h-full flex flex-col sm:flex-row justify-center items-center font-['Work_Sans'] p-16 bg-gradient-to-bl from-cyan-500 via-blue-600 to-purple-700 rounded-3xl shadow-2xl">

            <div className="sm:w-2/5 bg-gradient-to-tr from-white to-gray-100 p-10 rounded-3xl shadow-xl transition-transform transform hover:scale-110 duration-500">
                <h2 className="text-6xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-yellow-500">Get in Touch</h2>
                <p className="mt-4 text-gray-800 text-lg">Feel free to send us a message. We'd love to hear from you!</p>
                <form onSubmit={handleSubmit} className="flex flex-col mt-8 space-y-4">
                    <input onChange={handleChange} name="name" value={data.name} placeholder="Full Name" className="p-4 bg-white rounded-xl border border-gray-200 focus:ring-4 focus:ring-blue-400 shadow-md outline-none" type="text" />
                    <input onChange={handleChange} name="email" value={data.email} placeholder="Email" className="p-4 bg-white rounded-xl border border-gray-200 focus:ring-4 focus:ring-blue-400 shadow-md outline-none" type="email" />
                    <textarea onChange={handleChange} name="message" value={data.message} placeholder="Your Message" rows="5" className="p-4 bg-white rounded-xl border border-gray-200 focus:ring-4 focus:ring-blue-400 shadow-md outline-none" />
                    <button type="submit" className="p-4 bg-gradient-to-r from-green-400 to-blue-500 text-white font-bold rounded-xl shadow-xl hover:opacity-90 transition">Send Message</button>
                </form>
            </div>

            <div className="hidden sm:flex items-center justify-center w-2/5 ml-10">
                <img className="rounded-3xl shadow-xl transition-transform transform hover:scale-125 duration-500" alt="Contact Us" src="https://i.ibb.co/P1zYDgh/image.jpg" />
            </div>
        </div>
    );
}
