import { useState } from "react";
import emailjs from "emailjs-com";
import { successToast, Toast } from "../util/toast";

export default function Contact(props) {

    const [data, setData] = useState({
        name: "",
        email: "",
        message: ""
    });

    function handleChange(event) {
        setData(prev => ({
            ...prev,
            [event.target.name]: event.target.value
        }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (!validateData()) return;
        props.setLoading(true);

        const templateParams = {
            from_name: data.name,
            from_email: data.email,
            message: data.message
        };

        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams, 'YOUR_USER_ID')
            .then(() => {
                props.setLoading(false);
                successToast("Message Sent");
                clearData();
            })
            .catch(err => {
                props.setLoading(false);
                Toast("Failed to send message");
            });
    }

    function clearData() {
        setData({
            name: "",
            email: "",
            message: ""
        });
    }

    function validateData() {
        if (data.name.length < 3) {
            Toast("Invalid Name");
            return false;
        }
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
            Toast("Invalid Email");
            return false;
        }
        if (data.message.length < 3) {
            Toast("Enter a valid message");
            return false;
        }
        return true;
    }

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-100 to-indigo-100 font-sans">
            <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-2xl">
                <h2 className="text-3xl font-semibold text-gray-800 mb-6 text-center">Get in Touch</h2>
                <p className="text-gray-600 text-center mb-8">We're here to help! Send us a message and we'll get back to you as soon as possible.</p>

                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Name</label>
                        <input onChange={handleChange} name="name" value={data.name} type="text" className="mt-1 block w-full p-3 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" placeholder="Your Name" />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input onChange={handleChange} name="email" value={data.email} type="email" className="mt-1 block w-full p-3 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" placeholder="Your Email" />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700">Message</label>
                        <textarea onChange={handleChange} name="message" value={data.message} rows="4" className="mt-1 block w-full p-3 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" placeholder="Your Message"></textarea>
                    </div>

                    <div>
                        <button type="submit" className="w-full py-3 px-4 bg-indigo-600 text-white font-semibold rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">Send Message</button>
                    </div>
                </form>
            </div>
        </div>
    );
}
