import {faUnlock} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import AttackBlock from "./Items/AttackBlock";
import Digest from "./Digest";

export default function Home() {

    function handleKnowMore() {
        const element = document.getElementById('home--2')
        if (element) element.scrollIntoView({behavior: "smooth"})
    }

    return (
        <div className="bg-black text-neon-green min-h-screen font-['VT323']">
        {/* HEADER SECTION */}
        <div className="flex flex-col md:flex-row justify-around mt-8 sm:mt-16 px-6 sm:px-20">
            {/* IMAGE FOR MOBILE */}
            <div className="sm:hidden flex justify-center">
                <img alt="" className="rounded-[25px] w-[90%] border-2 border-neon-green shadow-neon" src="https://i.pinimg.com/736x/67/a1/a1/67a1a1b9b21505a42648010e3669076e.jpg"/>
            </div>

            {/* INFO SECTION */}
            <div className="ml-8 mt-8 text-neon-green">
                <p className="text-4xl sm:text-6xl font-bold glitch animate-pulse">Discover </p>
                <p className="text-4xl sm:text-6xl font-bold glitch animate-pulse">Graphical Password</p>
                <p className="text-4xl sm:text-6xl font-bold glitch animate-pulse">Authentication</p>
                <p className="text-lg sm:text-2xl mt-6 text-neon-light">A Cybersecurity Approach for Hack-Proof Protection</p>
                <p className="text-lg sm:text-2xl">Enhanced Security & User Experience</p>
                <button 
                    onClick={handleKnowMore} 
                    className="transition duration-500 ease-in-out sm:w-1/3 bg-neon-green text-black rounded-lg px-4 py-2 mt-6 sm:text-xl border-neon-green border-2 hover:bg-black hover:text-neon-green hover:shadow-neon-glow">
                    <FontAwesomeIcon className="mr-3" icon={faUnlock} />
                    Know More
                </button>
            </div>

            {/* IMAGE FOR DESKTOP */}
            <div className="hidden sm:block">
                <img alt="" className="hover:scale-95 transition duration-500 ease-in-out hover:shadow-neon rounded-[25px] w-full border-2 border-neon-green" src="https://i.pinimg.com/736x/67/a1/a1/67a1a1b9b21505a42648010e3669076e.jpg"/>
            </div>
        </div>

        {/* ATTACK RESISTANCE SECTION */}
        <div id="home--2" className="sm:mt-48 px-6 sm:px-20">
            <div className="pt-24">
                <p className="text-4xl sm:text-5xl font-bold text-neon-green glitch animate-pulse">Resistance to Cyber Attacks</p>
                <p className="text-lg sm:text-2xl mt-3 text-neon-light">Our system safeguards against cutting-edge threats.</p>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 mt-8">
                    <AttackBlock icon="https://img.icons8.com/ios-filled/100/A259FF/re-enter-pincode.png" title="Bruteforce" desc="Advanced lockout mechanisms and notification alerts." />
                    <AttackBlock icon="https://img.icons8.com/ios-filled/100/A259FF/show-password.png" title="Shoulder Surfing" desc="Innovative graphical password structure resists over-the-shoulder spying." />
                    <AttackBlock icon="https://img.icons8.com/ios-filled/100/A259FF/spyware-free.png" title="Spyware" desc="Graphical authentication prevents keylogging threats." />
                    <AttackBlock icon="https://img.icons8.com/ios-filled/100/A259FF/enter-pin.png" title="Phishing" desc="Unique graphical elements make fake login attempts ineffective." />
                </div>
            </div>
        </div>

        <Digest/>
    </div>

    )
}
